-- Usage: 
-- 1st time install:
-- mysql -uroot -pXXX 
-- GRANT all on design.* to evil@'%' identified by '1';
-- GRANT all on design.* to evil@'localhost' identified by '1';
-- 
-- 2nd time:
-- mysql -uevil -p1 < db-design.sql
--
-- peter note: change password '1' to other password !
-- after this, we may do:
-- mysql -uevil -p1 design
-- or remotely:
-- mysql -uevil -p1 -h192.168.1.30 design

DROP DATABASE IF EXISTS evil_design;

CREATE DATABASE IF NOT EXISTS evil_design CHARACTER SET 'utf8' COLLATE 'utf8_general_ci';
-- 'utf8_bin';
SET NAMES utf8;
SET default_storage_engine=MYISAM;

USE evil_design;

-- ----------- shop ---------

CREATE TABLE design_shop (
	card_id INT UNSIGNED NOT NULL PRIMARY KEY
,	card_buy_gold INT UNSIGNED NOT NULL DEFAULT 200
,	card_sell_gold INT UNSIGNED NOT NULL DEFAULT 100
,	card_buy_crystal INT UNSIGNED NOT NULL DEFAULT 20
,	card_sell_crystal INT UNSIGNED NOT NULL DEFAULT 15
,	piece_buy_gold INT UNSIGNED NOT NULL DEFAULT 0
,	piece_sell_gold INT UNSIGNED NOT NULL DEFAULT 10
,	piece_buy_crystal INT UNSIGNED NOT NULL DEFAULT 2
,	piece_sell_crystal INT UNSIGNED NOT NULL DEFAULT 1
);


-- id = 1 to 20 is the first level of ai
-- id = 21 to 40 is the second level of ai
CREATE TABLE design_ai (
	id INT UNSIGNED NOT NULL PRIMARY KEY 
,	icon INT UNSIGNED NOT NULL DEFAULT 0
,	lv INT UNSIGNED NOT NULL DEFAULT 1
,	rating DOUBLE NOT NULL DEFAULT 1000
,	rating_flag TINYINT UNSIGNED NOT NULL DEFAULT 0 -- 0==not count rating in win_game, 1==count rating
,	pid INT UNSIGNED NOT NULL DEFAULT 0 -- prize card id
,	win_gold INT UNSIGNED NOT NULL DEFAULT 0
,	win_exp INT UNSIGNED NOT NULL DEFAULT 0
,	alias	CHAR(30) NOT NULL DEFAULT 'AI'
,	deck VARCHAR(401) NOT NULL
);


CREATE TABLE design_constant (
	batch_refresh_gold 		INT UNSIGNED NOT NULL DEFAULT 0
,	pick_gold 				INT UNSIGNED NOT NULL DEFAULT 0
,	batch_refresh_crystal 	INT UNSIGNED NOT NULL DEFAULT 0
,	pick_crystal 			INT UNSIGNED NOT NULL DEFAULT 0
,	max_timeout 			INT UNSIGNED NOT NULL DEFAULT 0

,	guild_bonus_rate		DOUBLE NOT NULL DEFAULT 0.05
,	create_guild_gold		INT UNSIGNED NOT NULL DEFAULT 0
,	create_guild_crystal	INT UNSIGNED NOT NULL DEFAULT 0
,	lottery_one_gold		INT UNSIGNED NOT NULL DEFAULT 0
,	lottery_one_crystal		INT UNSIGNED NOT NULL DEFAULT 0

,	lottery_ten_gold		INT UNSIGNED NOT NULL DEFAULT 0
,	lottery_ten_crystal		INT UNSIGNED NOT NULL DEFAULT 0
,	first_pay_double		INT UNSIGNED NOT NULL DEFAULT 0
,	monthly_increase_date	INT UNSIGNED NOT NULL DEFAULT 0
