CREATE TABLE design_exp (
	lv		INT UNSIGNED UNIQUE NOT NULL
,	exp		INT UNSIGNED NOT NULL
);


-- gold_shuffle=10, crystal_shuffle=1  
CREATE TABLE design_pick (
	ptype TINYINT NOT NULL  -- 0=gold_pick, 1=crystal_pick
,	loc TINYINT NOT NULL
,	rate	INT	NOT NULL	-- rate of picking this loc
,	star1	INT NOT NULL
,	star2	INT NOT NULL
,	star3	INT NOT NULL
,	star4	INT NOT NULL
,	star5	INT NOT NULL
,	PRIMARY KEY (ptype, loc)
) ;

CREATE TABLE design_lottery (
	team		INT NOT NULL -- 1 for normal lottery; 2 for special lottery
,	money_type	INT NOT NULL -- 1 for gold; 2 for crystal
,	weight		INT NOT NULL
,	cards		VARCHAR(2000) NOT NULL
,	PRIMARY KEY (team, money_type, weight)
);

CREATE TABLE design_notice (
	id INT UNSIGNED NOT NULL PRIMARY KEY 
,	create_date	DATETIME NOT NULL
,	title	VARCHAR(100) NOT NULL
,	notice	VARCHAR(500) NOT NULL
) DEFAULT CHARSET = utf8 ;

CREATE TABLE design_std_deck (
	id INT UNSIGNED PRIMARY KEY NOT NULL
,	deck VARCHAR(401) NOT NULL
) DEFAULT CHARSET = utf8 ;

CREATE TABLE design_hero_slot (
	hero_id INT UNSIGNED PRIMARY KEY NOT NULL
,	deck VARCHAR(401) NOT NULL
) DEFAULT CHARSET = utf8 ;


CREATE TABLE design_guild(
	lv		INT UNSIGNED PRIMARY KEY NOT NULL
,	gold	INT UNSIGNED NOT NULL DEFAULT 0
,	levelup_gold INT UNSIGNED NOT NULL DEFAULT 0
,	member_max INT UNSIGNED NOT NULL DEFAULT 100
);


CREATE TABLE design_merge (
	card_id INT UNSIGNED PRIMARY KEY NOT NULL 
,	count INT UNSIGNED NOT NULL DEFAULT 0
,	gold INT UNSIGNED NOT NULL DEFAULT 0
,	crystal INT UNSIGNED NOT NULL DEFAULT 0
);


-- TODO pay_type -> channel, pay_price -> (int) price
CREATE TABLE design_pay (
	pay_id INT UNSIGNED PRIMARY KEY NOT NULL 
,	pay_code INT UNSIGNED NOT NULL DEFAULT 0 
,	channel INT UNSIGNED NOT NULL DEFAULT 0
,	price INT UNSIGNED NOT NULL DEFAULT 0
,	money_type INT UNSIGNED NOT NULL DEFAULT 1
,	money INT UNSIGNED NOT NULL DEFAULT 0
,	title VARCHAR(500) NOT NULL DEFAULT ''
,	description	VARCHAR(500) NOT NULL DEFAULT ''
) DEFAULT CHARSET = utf8 ;

CREATE TABLE design_monthly (
	pay_code INT UNSIGNED PRIMARY KEY NOT NULL
) DEFAULT CHARSET = utf8 ;


CREATE TABLE design_version (
	id INT UNSIGNED NOT NULL PRIMARY KEY 
,	game_version INT UNSIGNED NOT NULL DEFAULT 0
,	client_version INT UNSIGNED NOT NULL DEFAULT 0
);

CREATE TABLE design_website (
	device_id INT UNSIGNED NOT NULL PRIMARY KEY 
,	website	VARCHAR(100) NOT NULL default '_'
,	note	VARCHAR(100) NOT NULL default '_'
) DEFAULT CHARSET = utf8 ;


-- mtype = MISSION_LEVEL 1, p1=level
-- mtype = MISSION_AI 2, p1=number of win game, p2=ai_id(design_ai)
-- mtype = MISSION_VS 3, p1=number of win game
-- mtype = MISSION_CHALLENGE 4, p1=number of win game in challenge
-- mtype = MISSION_BEI_CHALLENGE 5, p1=number of win game in bei challenge
-- mtype = MISSION_REPLAY 6, p1=number of replay view
-- mtype = MISSION_CHAT 7, p1=channel(1=world,2=guild,3=room)  (n1 = 1or0)
-- mtype = MISSION_FRIEND 8, p1=number of friends
-- mtype = MISSION_SHOP 9, p1=buy card count, p2=card_id
-- mtype = MISSION_CARD 10, p1=own card count, p2=card_id
-- mtype = MISSION_COLLECTION 11, p1=collection count
-- mtype = MISSION_PROMOTION 12, p1=valid promotion count

CREATE TABLE design_mission (
	mid INT UNSIGNED NOT NULL PRIMARY KEY
,	pre	INT UNSIGNED NOT NULL DEFAULT 0 -- pre_mission_id
,	lv INT UNSIGNED NOT NULL DEFAULT 0 -- minimum level 
,	hero INT UNSIGNED NOT NULL DEFAULT 0 -- pre: need to hv this hero 
,	daily INT UNSIGNED NOT NULL DEFAULT 0 -- 1 for daily mission ref:reset_time

,	guild_lv INT UNSIGNED NOT NULL DEFAULT 0 -- guild lv
,	mtype INT UNSIGNED NOT NULL DEFAULT 0 -- 
,	p1 INT UNSIGNED NOT NULL DEFAULT 0 -- 
,	p2 INT UNSIGNED NOT NULL DEFAULT 0 -- 
,	p3 INT UNSIGNED NOT NULL DEFAULT 0 -- 

,	reward_exp INT UNSIGNED NOT NULL DEFAULT 0 -- 
,	reward_gold INT UNSIGNED NOT NULL DEFAULT 0 -- 
,	reward_crystal INT UNSIGNED NOT NULL DEFAULT 0 -- 
,	reward_power INT UNSIGNED NOT NULL DEFAULT 0
,	reward_card CHAR(50) NOT NULL DEFAULT '' -- 
,	reward_piece CHAR(50) NOT NULL DEFAULT ''

,	reset_time DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP
,	mtext VARCHAR(100) NOT NULL default '_'
) DEFAULT CHARSET = utf8 ;

CREATE TABLE design_slot (
	id INT UNSIGNED NOT NULL PRIMARY KEY
,	gold INT UNSIGNED NOT NULL DEFAULT 0
,	ctystal INT UNSIGNED NOT NULL DEFAULT 0
);


CREATE TABLE design_rank_reward (
	id 				INT UNSIGNED NOT NULL PRIMARY KEY
,	start_num		INT UNSIGNED NOT NULL	
,	end_num			INT UNSIGNED NOT NULL	
,	gold 			INT UNSIGNED NOT NULL DEFAULT 0
,	crystal 		INT UNSIGNED NOT NULL DEFAULT 0
,	title			VARCHAR(100) NOT NULL DEFAULT '-'
,	message			VARCHAR(300) NOT NULL DEFAULT '-'
);

CREATE TABLE design_rank_time (
	id 				INT UNSIGNED NOT NULL PRIMARY KEY
,	time			INT UNSIGNED NOT NULL	-- 0 -> 00:00, 2000 -> 20:00, 2200 -> 22:00
);


CREATE TABLE design_arena_reward (
	id 				INT UNSIGNED NOT NULL PRIMARY KEY
,	start_num		INT UNSIGNED NOT NULL	
,	end_num			INT UNSIGNED NOT NULL	
,	gold 			INT UNSIGNED NOT NULL DEFAULT 0
,	crystal 		INT UNSIGNED NOT NULL DEFAULT 0
,	title			VARCHAR(100) NOT NULL DEFAULT '-'
,	message			VARCHAR(300) NOT NULL DEFAULT '-'
);



CREATE TABLE design_rate_card (
	team_id			INT UNSIGNED NOT NULL
,	weight			INT UNSIGNED NOT NULL
,	range_list		VARCHAR(2000) NOT NULL DEFAULT '' -- card_id card_id ...
,	PRIMARY KEY(team_id, weight)
);


CREATE TABLE design_gift (
	id				INT UNSIGNED NOT NULL PRIMARY KEY
,	channel			INT UNSIGNED NOT NULL
,	gtype			INT UNSIGNED NOT NULL
,	gold			INT UNSIGNED NOT NULL DEFAULT 0
,	crystal			INT UNSIGNED NOT NULL DEFAULT 0
,	ran_team_list	VARCHAR(1000) NOT NULL DEFAULT '' -- id list in design_random
);


CREATE TABLE design_gate (
	gate_id		INT UNSIGNED NOT NULL PRIMARY KEY
,	title		VARCHAR(100) NOT NULL DEFAULT '_'
,	gold		INT UNSIGNED NOT NULL DEFAULT 0
,	crystal		INT UNSIGNED NOT NULL DEFAULT 0
,	exp			INT UNSIGNED NOT NULL DEFAULT 0
,	focus_card	INT UNSIGNED NOT NULL DEFAULT 0
,	power		INT UNSIGNED NOT NULL DEFAULT 0
,	gate_info	VARCHAR(400) NOT NULL DEFAULT '' 
);

CREATE TABLE design_fight_schedule (
	game_type		INT UNSIGNED NOT NULL
,	open_time		TIME NOT NULL DEFAULT 0
,	close_time		TIME NOT NULL DEFAULT 0
,	PRIMARY KEY(game_type, open_time, close_time)
);

CREATE TABLE design_gate_msg (
	gate_id		INT UNSIGNED NOT NULL 
,	round		INT UNSIGNED NOT NULL
,	card_id		INT UNSIGNED NOT NULL DEFAULT 0
,	msg			VARCHAR(200) NOT NULL DEFAULT '_' 
,	PRIMARY KEY(gate_id, round)
);


CREATE TABLE design_tower_reward (
	id 				INT UNSIGNED NOT NULL PRIMARY KEY
,	start_num		INT UNSIGNED NOT NULL	
,	end_num			INT UNSIGNED NOT NULL	
,	battle_coin 	INT UNSIGNED NOT NULL DEFAULT 0
);


CREATE TABLE design_piece_shop (
	id				INT UNSIGNED NOT NULL PRIMARY KEY
,	card_id			INT UNSIGNED NOT NULL DEFAULT 0
,	count			INT UNSIGNED NOT NULL DEFAULT 0
,	gold			INT UNSIGNED NOT NULL DEFAULT 0
,	crystal			INT UNSIGNED NOT NULL DEFAULT 0
,	weight			INT UNSIGNED NOT NULL DEFAULT 0
,	hard_show		INT UNSIGNED NOT NULL DEFAULT 0
);

CREATE TABLE design_card_chapter (
	card_id			INT UNSIGNED NOT NULL
,	chapter_name	CHAR(50) NOT NULL DEFAULT '_'
,	chapter_id		INT UNSIGNED NOT NULL DEFAULT 1
,	stage_id		INT UNSIGNED NOT NULL DEFAULT 1
,	PRIMARY KEY(card_id, chapter_id, stage_id)
);


-- note how to set target:
-- situation 1, check range, p1 and p2 is range, if p1/p2 = 99, means p1/p2 is useless
-- 1. p1=0, p2=10,		p1<=counter<=p2 is ok
-- 2. p1=99, p2=10,		counter>=p2 is ok
-- 3. p1=10, p2=99,		counter<=p1 is ok
-- situation 2, for count card, p1 is card_id, use p2 get check_type and num
-- type = p2/10, num = p2%10, so num only can be 0~9
-- 1. type = 0, same type, counter == num is ok, e.g. p1=23, p2=3: type=0, num=3
-- 2. type = 1, less type, counter <= num is ok, e.g. p1=23, p2=13: type=1, num=3
-- 3. type = 2, more type, counter >= num is ok, e.g. p1=23, p2=23: type=2, num=3
CREATE TABLE design_solo (
    id          INT UNSIGNED NOT NULL PRIMARY KEY
,   hero_id     INT UNSIGNED NOT NULL DEFAULT 1 -- solo ai hero_id
,   alias       CHAR(30) NOT NULL DEFAULT 'solo'
,   solo_type   INT UNSIGNED NOT NULL DEFAULT 0 
-- NOTE: solo_type =  flag_shuffle_deck1 		* 1000 
--					+ flag_shuffle_deck2 		* 100 
--					+ flag_teach 				* 10 
--					+ flag_solo_type			* 1
,   max_ally    INT UNSIGNED NOT NULL DEFAULT 0
,   max_hp		INT UNSIGNED NOT NULL DEFAULT 0
,   deck        VARCHAR(401) NOT NULL
,   type_list   CHAR(100) NOT NULL -- to control solo ai play hand card
,   my_hero		INT UNSIGNED NOT NULL DEFAULT 0 -- 0 means use my real hero
,   my_hp		INT UNSIGNED NOT NULL DEFAULT 0 
,   my_deck		VARCHAR(401) NOT NULL -- add for my deck, empty means use real deck
,   start_side	INT UNSIGNED NOT NULL DEFAULT 0

,   target1			INT UNSIGNED NOT NULL DEFAULT 0
,   p11				INT UNSIGNED NOT NULL DEFAULT 0
,   p12				INT UNSIGNED NOT NULL DEFAULT 0
,   target2			INT UNSIGNED NOT NULL DEFAULT 0
,   p21				INT UNSIGNED NOT NULL DEFAULT 0
,   p22				INT UNSIGNED NOT NULL DEFAULT 0
,   target3			INT UNSIGNED NOT NULL DEFAULT 0
,   p31				INT UNSIGNED NOT NULL DEFAULT 0
,   p32				INT UNSIGNED NOT NULL DEFAULT 0
);


CREATE TABLE design_robot (
    id          INT UNSIGNED NOT NULL
,	rtype 		INT UNSIGNED NOT NULL DEFAULT 0 -- 0 for normal robot, 1 for fakeman
,	icon 		INT UNSIGNED NOT NULL DEFAULT 0
,	lv 			INT UNSIGNED NOT NULL DEFAULT 1
,	rating 		DOUBLE NOT NULL DEFAULT 1000
,   hero_id     INT UNSIGNED NOT NULL DEFAULT 1
,   hp			INT UNSIGNED NOT NULL DEFAULT 0
,   energy		INT UNSIGNED NOT NULL DEFAULT 0
,   alias       CHAR(30) NOT NULL DEFAULT 'robot'
,   deck        VARCHAR(401) NOT NULL
,	PRIMARY KEY (id, rtype)
);


CREATE TABLE design_chapter (
	chapter_id		INT UNSIGNED NOT NULL DEFAULT 1
,   stage_id		INT UNSIGNED NOT NULL DEFAULT 1
,   chapter_name    CHAR(100) NOT NULL DEFAULT 'chapter'
,   stage_name      CHAR(100) NOT NULL DEFAULT 'stage'
,   stage_msg      VARCHAR(400) NOT NULL DEFAULT '-'

,   solo_list		CHAR(30) NOT NULL DEFAULT '1 2 3'  -- pick one solo AI
,   exp				INT UNSIGNED NOT NULL DEFAULT 0
,   power			INT UNSIGNED NOT NULL DEFAULT 0 -- power consumed
,   tips_list		CHAR(30) NOT NULL DEFAULT '0' -- tips card

,   reward1			INT UNSIGNED NOT NULL DEFAULT 0
,   r1_count		INT UNSIGNED NOT NULL DEFAULT 0 -- TODO r1_count
,   r1_weight		INT UNSIGNED NOT NULL DEFAULT 0
,   reward2			INT UNSIGNED NOT NULL DEFAULT 0
,   r2_count		INT UNSIGNED NOT NULL DEFAULT 0
,   r2_weight		INT UNSIGNED NOT NULL DEFAULT 0
,   reward3			INT UNSIGNED NOT NULL DEFAULT 0
,   r3_count		INT UNSIGNED NOT NULL DEFAULT 0
,   r3_weight		INT UNSIGNED NOT NULL DEFAULT 0
,   reward4			INT UNSIGNED NOT NULL DEFAULT 0
,   r4_count		INT UNSIGNED NOT NULL DEFAULT 0
,   r4_weight		INT UNSIGNED NOT NULL DEFAULT 0
,	PRIMARY KEY (chapter_id, stage_id)
);

CREATE TABLE design_hero (
	hero_id			INT UNSIGNED NOT NULL PRIMARY KEY
,   hp				INT UNSIGNED NOT NULL DEFAULT 0
,   energy			INT UNSIGNED NOT NULL DEFAULT 0
);

CREATE TABLE design_hero_mission (
	hero_id			INT UNSIGNED NOT NULL DEFAULT 1
,	mission_id		INT UNSIGNED NOT NULL DEFAULT 1
,	pre				INT UNSIGNED NOT NULL DEFAULT 0 
,   reward_type		INT UNSIGNED NOT NULL DEFAULT 1
,   reward_count	INT UNSIGNED NOT NULL DEFAULT 0
,   mtype			INT UNSIGNED NOT NULL DEFAULT 1
,   p1				INT UNSIGNED NOT NULL DEFAULT 0
,   p2				INT UNSIGNED NOT NULL DEFAULT 0
,   p3				INT UNSIGNED NOT NULL DEFAULT 0
,   msg      		VARCHAR(400) NOT NULL DEFAULT '-'
,	PRIMARY KEY (hero_id, mission_id)
);

CREATE TABLE design_achievement (
	aid 	INT UNSIGNED NOT NULL PRIMARY KEY
,	name 	VARCHAR(100) NOT NULL default '_'
,	msg 	VARCHAR(200) NOT NULL default '_'

,	pre		INT UNSIGNED NOT NULL DEFAULT 0 -- pre_aid
,	atype 	INT UNSIGNED NOT NULL DEFAULT 0 -- 
,	p1 		INT UNSIGNED NOT NULL DEFAULT 0 -- 
,	p2 		INT UNSIGNED NOT NULL DEFAULT 0 -- 
,	p3 		VARCHAR(401) NOT NULL DEFAULT '_'

,	reward_gold			INT UNSIGNED NOT NULL DEFAULT 0 -- 
,	reward_crystal 		INT UNSIGNED NOT NULL DEFAULT 0 -- 
,	reward_card 		VARCHAR(50) NOT NULL 
,	reward_piece 		VARCHAR(50) NOT NULL 
) DEFAULT CHARSET = utf8 ;


CREATE TABLE design_daily_login (
	day			INT UNSIGNED NOT NULL
,	log_time	INT UNSIGNED NOT NULL DEFAULT 0
,	gold		INT UNSIGNED NOT NULL DEFAULT 0
,	crystal		INT UNSIGNED NOT NULL DEFAULT 0
,	cards		CHAR(50) NOT NULL DEFAULT ''
,	pieces		CHAR(50) NOT NULL DEFAULT ''
,	PRIMARY KEY(day, log_time)
);

CREATE TABLE design_quick_reward (
	win_flag			INT UNSIGNED NOT NULL DEFAULT 0 -- 1 for winner, 2 for loser
,	reward_type			INT UNSIGNED NOT NULL DEFAULT 0 -- 1 for gold, 2 for crystal
,	count				INT UNSIGNED NOT NULL DEFAULT 0
,	weight				INT UNSIGNED NOT NULL DEFAULT 0
);


CREATE TABLE design_chapter_dialog (
	chapter		INT UNSIGNED NOT NULL DEFAULT 0
,	stage		INT UNSIGNED NOT NULL DEFAULT 0
,	dialog		VARCHAR(1000) NOT NULL DEFAULT ''
,	PRIMARY KEY(chapter, stage)
) DEFAULT CHARSET = utf8;


